    private void oddsBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_oddsBtnActionPerformed
        WCOddsToWin wc = new WCOddsToWin("Brazil", "\n Group A", "\n They are 3/1 to win The World Cup on home soil !");

        JOptionPane.showMessageDialog(null, "" + wc.getTeamName() + "" + wc.getTeamGroup() + "" + wc.getOdds());
    }//GEN-LAST:event_oddsBtnActionPerformed

